import { useEffect } from "react"
import { useDispatch, useSelector } from "react-redux"
import { fetchCatsById } from "../../redux/action"
import { Card } from "./Card"
import "./styles.scss"




export const Cards = () => {

    const dispatch = useDispatch()
    const {cats} = useSelector((s) => s.reducer)

    useEffect(() => {
        dispatch(fetchCatsById())
    })


    const cards = cats.length ? cats.map((cat) => {
        return (
            <p id={cat.id} onClick={dispatch(fetchCatsById(10, 1, cat.id))}>{cat.name}</p>
        )
    }): 'error'
    return(
        <div className="cards">
            {cards}
        </div>
    )
}
