import { FETCH_CATEGORIES } from "./types";

const initState = ({
    categories: []
})

export const reducer = (state = initState, action) => {
    switch (action.type) {
        case FETCH_CATEGORIES:
            return{...state, categories: action.payload}



        default:
            return state

    }

}
