import { FETCH_CATEGORIES, FETCH_CATS_BI_ID } from "./types";

export const fetchCatsById = async(limit, page, id, dispatch) => {
    const getPost = await fetch(`https://api.thecatapi.com/v1/images/search?limit=${limit}&page=${page}_ids=${id}`)
        const data = await getPost.json()
    dispatch({type: FETCH_CATS_BI_ID, payload: data})


}
export const fetchCategories = async(dispatch) => {
    const fetchData = await fetch('https://api.thecatapi.com/v1/categories')
    const data = await fetchData.json()
    dispatch({type: FETCH_CATEGORIES, payload: data})
}
