import { FETCH_CATEGORIES, FETCH_CATS_BI_ID } from "./types";

export const fetchCatsById = async(limit, page, id) => {
    const getPost = await fetch(`https://api.thecatapi.com/v1/images/search?limit=${limit}&page=${page}_ids=${id}`)
        const data = await getPost.json()
        return{
        type: FETCH_CATS_BI_ID,
        payload: data
        }

}
export const fetchCategories = async() => {
    const fetchData = fetch('https://api.thecatapi.com/v1/categories')
    const data = fetchData.json()
    return{
        type: FETCH_CATEGORIES,
        payload: data
    }
}
